options.value:
{
  application-name = gotosocial;
  bind-address = 127.0.0.1;
  db-address = /var/lib/gotosocial/database.sqlite;
  db-type = sqlite;
  port = 8080;
  protocol = https;
  storage-local-base-path = /var/lib/gotosocial/storage;
}

if lib.versionOlder config.system.stateVersion 24.05
then $\{httpConf.scheme}://$\{httpConf.host}:$\{builtins.toString httpConf.port}/media/
else null;

{
  adapter = (pkgs.formats.elixirConf { }).lib.mkRaw Ecto.Adapters.Postgres;
  socket_dir = /run/postgresql;
  username = config.services.akkoma.user;
  database = akkoma;
}

if lib.versionOlder config.system.stateVersion 24.05
then $\{httpConf.scheme}://$\{httpConf.host}:$\{builtins.toString httpConf.port}
else null;

lib.mapAttrs (key: val:
  (pkgs.formats.elixirConf { }).lib.mkMap { name = val.name; ref = val.ref; })
  config.services.akkoma.frontends;

{
  cache-path = /var/cache/gonic;
  listen-addr = 127.0.0.1:4747;
  tls-cert = null;
  tls-key = null;
}

{
  api = {
    allowed_regions = [
      AT
      DE
      CH
    ];
    enabled = true;
    filter_regions = true;
    url = https://api.invidious.io/instances.json;
  };
  app = {
    enable_youtube_fallback = false;
    listen = 127.0.0.1:8050;
    reload_instance_list_interval = 60s;
  };
  healthcheck = {
    allowed_status_codes = [
      200
    ];
    filter_by_response_time = {
      enabled = true;
      qty_of_top_results = 3;
    };
    interval = 10s;
    minimum_ratio = 0.2;
    path = /;
    remove_no_ratio = true;
    text_not_present = YouTube is currently trying to block Invidious instances;
    timeout = 1s;
  };
}

{
  searchd = {
    data_dir = /var/lib/manticore;
    listen = [
      127.0.0.1:9312
      127.0.0.1:9306:mysql
      127.0.0.1:9308:http
    ];
    log = /var/log/manticore/searchd.log;
    pid_file = /run/manticore/searchd.pid;
    query_log = /var/log/manticore/query.log;
  };
}
